{
  "name": "ValidationCase{{$randomInt}}{{$randomInt}}",
  "baseType": "Page",
  "properties": [
    {
      "name": "Heading",
      "dataType": "PropertyString",
      "required": true,
      "branchSpecific": true,
      "validation": [
        {
          "name": "Length",
          "minimum": 2, 
          "maximum": 100,
          "severity": "error",
          "errorMessage": "This is the customized message. The heading length should be bettween 2 and 100."
        },
        {
          "name": "RegularExpression",
          "severity": "error",
          "pattern": "^epi[\\w ]+"
        }
      ]
    },
    {
      "name": "IntegerList",
      "dataType": "PropertyIntegerList",
      "branchSpecific": true,
      "required": true,
      "validation": [
        {
          "name": "ItemRange",
          "severity": "error",
          "minimum": 1, 
          "maximum": 10
        }
      ]
    },
    {
      "name": "EventDate",
      "dataType": "PropertyDate",
      "branchSpecific": true,
      "required": true,
      "validation": [
        {
          "name": "Range",
          "severity": "error",
          "minimum": "2020-01-01T00:00:00Z", 
          "maximum": "2022-12-31T00:00:00Z" 
        }
      ]
    }
  ]
}

