    },
    "metaDescription": {
        "value": "Projects have a natural lifecycle with well-defined stages. Progress monitoring is a critical part of making sure that your project is on-track and capable of delivering in the end.  ",
        "propertyDataType": "PropertyLongString"
    },
    "hideSiteFooter": {
        "value": null,
        "propertyDataType": "PropertyBoolean"
